import { FormControl, FormsModule, ReactiveFormsModule, } from '@angular/forms';
import { moduleMetadata } from '@storybook/angular';
import Documentation from './documentation.mdx';
export default {
    title: 'Components/Form/Input',
    decorators: [
        moduleMetadata({
            imports: [FormsModule, ReactiveFormsModule],
        }),
    ],
    parameters: {
        docs: {
            page: Documentation,
        },
        componentIds: ['component-input'],
    },
};
const Template = (args) => {
    return {
        template: `
      <div class="mb-5">Value: {{ value }}</div>
      <input [value]="value" (input)="value = $event.target.value"/>
    `,
        props: {
            ...args,
            value: 'Hello',
        },
    };
};
export const Default = Template.bind({});
const TemplateTemplateDriven = (args) => {
    return {
        template: `
      <div class="mb-5">Value: {{ value }}</div>
      <input [(ngModel)]="value"/>
    `,
        props: {
            ...args,
            value: 'Hello',
        },
    };
};
export const TemplateDriven = TemplateTemplateDriven.bind({});
const TemplateReactiveForms = (args) => {
    return {
        template: `
      <div class="mb-5">Value: {{ control.value }}</div>
      <input [formControl]="control" />
    `,
        props: {
            ...args,
            control: new FormControl('Hello'),
        },
    };
};
export const ReactiveForms = TemplateReactiveForms.bind({});
